import styled from 'styled-components';
import logo from '../logo.png';
import '../fonts.css';


const Wrapper = styled.div`
display:flex;
background-color: rgb(38,40,56,0.93);
height:100vh;
flex-direction: column;
align-items:center;
justify-content:space-evenly;
`;

const Description = styled.h2`
font-family: 'Montserrat';
font-style: normal;
font-weight: 700;
font-size: 22px;
line-height: 40px;
color:#FFFFFF
`;

const LobbyText = styled.h1`
color:#FFFFFF;
`;

const Logo = () => <img src={logo} alt="logo"/>

function JoinRoom({ joinRoom, roomCode, setRoomCode, newRoom }) {

  return (
    <Wrapper>
    <Logo></Logo>
    <Description>Team-Based Chess Game</Description>
      <form onSubmit={joinRoom}>
        <LobbyText>Join Current Lobby</LobbyText>
        <input
          type="text"
          placeholder="Enter Code Here"
          value={roomCode}
          onChange={(e) => setRoomCode(e.target.value)}
        />
        <button type="submit">Submit</button>
      </form>
      <form onSubmit={newRoom}>
        <LobbyText>Create New Lobby</LobbyText>
        <button type="submit">Create</button>
      </form>
    </Wrapper>
  );
};

export default JoinRoom;
