import styled from 'styled-components';
import logo from '../logo.png';

const Wrapper = styled.div`
display:flex;
background-color: rgb(38,40,56,0.93);
height:100vh;
flex-direction: column;
align-items:center;
justify-content:center;
`;

const Description = styled.h2`
font-family: 'Montserrat';
font-style: normal;
font-weight:700;
font-size: 22px;
line-height: 40px;
color:#FFFFFF
`;

const ControlPanel = styled.div`
display:flex;
background-color: #868BAC;
height:30vh;
width:35vw;
justify-content:space-around;
box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
border-radius: 15px;
`;

const Button = styled.button`
`

const Logo = () => <img src={logo} alt="logo" />

function JoinRoom({ joinRoom, roomCode, setRoomCode, newRoom }) {

  return (
    <Wrapper>
      <div>
        <Logo></Logo>
        <Description>TEAM-BASED CHESS GAME</Description>
      </div>
      <ControlPanel>
        <form onSubmit={joinRoom}>
          <Description>Join Current Lobby</Description>
          <input
            type="text"
            placeholder="Enter Code Here"
            value={roomCode}
            onChange={(e) => setRoomCode(e.target.value)}
          />
          <button type="submit">Submit</button>
        </form>
        <form onSubmit={newRoom}>
          <Description>Create New Lobby</Description>
          <button type="submit">Create</button>
        </form>
      </ControlPanel>
    </Wrapper>
  );
};

export default JoinRoom;
