import { useState, useEffect } from "react";
import Chessboard from "chessboardjsx";
import io from 'socket.io-client';
import {Chess} from "chess.js";

// const [message, setMessage] = useState("");
// const [messageReceived, setMessageReceived] = useState([]);

// const sendMessage = () => {
//   socket.emit("send_message", { message });
//   setMessage("");
// };

// useEffect(() => {
//   socket.on("receive_message", (data) => {
//     setMessageReceived(data);
//   });
// }, [socket])

const STOCKFISH = window.STOCKFISH;
// const socket = io.connect("http://localhost:3001");
const game = new Chess();

function App() {

  var score;
  const [position, setPosition] = useState("start");

  const onDrop = ({ sourceSquare, targetSquare }) => {
    const move = game.move({ from: sourceSquare, to: targetSquare })
    if (move === null) return; //illegal move  

    // grab player proposed position and show it
    const proposed = game.fen()
    setPosition(proposed);
    game.undo();
    console.log(position)
    //grab the rating of the move
    const rating = this.engineGame().evalMove(proposed)

    //socket.emit("move", rating);
  }

  const engineGame = (options) => {
    options = options || {};

    /// We can load Stockfish via Web Workers or via STOCKFISH() if loaded from a <script> tag.
    let engine =
      typeof STOCKFISH === "function"
        ? STOCKFISH()
        : new Worker(options.stockfishjs || "stockfish.js");
    let announced_game_over;

    setInterval(function () {
      if (announced_game_over) {
        return;
      }
      if (game.game_over()) {
        announced_game_over = true;
      }
    }, 500);

    const evalMove = (proposed) => {
      if (!game.game_over()) {
        engine.postMessage("ucinewgame");
        engine.postMessage("position fen " + proposed);
        engine.postMessage("eval");
      }
    };

    engine.onmessage = (event) => {
      let line;

      if (event && typeof event === "object") {
        line = event.data;
      } else {
        line = event;
      }

      if (line.substr(0, "Total Evaluation".length) === "Total Evaluation")
        score = line.substr(18).substr(0, 4);

        console.log(score); //testing
  };

  return {
    start: function () {
      engine.postMessage("ucinewgame");
      engine.postMessage("isready");
      announced_game_over = false;
    },
    evalMove: function () {
      evalMove();
    },
  };
};

// useEffect(() => {
//   socket.on("receive_move", (move) => {
//     if (move === null) return;
//     setPosition(move);
//   });
// }, [socket])

return (
  <div style={boardsContainer}>
    <Chessboard
      id="board!"
      position={position}
      width={320}
      onDrop={onDrop}
      boardStyle={boardStyle}
      orientation="white"
    />
  </div>
);
}

export default App;

const boardsContainer = {
  display: "flex",
  justifyContent: "space-around",
  alignItems: "center"
};
const boardStyle = {
  borderRadius: "5px",
  boxShadow: `0 5px 15px rgba(0, 0, 0, 0.5)`
};
