import styled from 'styled-components';
import { Logo, Wrapper } from "../StyledComponents";
import { useState, useEffect } from 'react';
import { MainLobbyPortal } from "../items/mainLobbyPortal";
import { UsernamePortal } from "../items/usernamePortal";
import { usePlayerContext } from '../contexts/PlayerContext';

function roomListeners(socket, setInRoom, setRoomCode, setIsWhite, setisHomescreen) {

    socket.on("room_joined", (roomCode, isWhite) => {
      console.log(roomCode);
      console.log("hello");
      setRoomCode(roomCode);
      setInRoom(true);
      setIsWhite(isWhite);
    });

    socket.on("room_code", (roomCode) => {
      setRoomCode(roomCode);
    });

    socket.on("yes_room", () => setisHomescreen(false));

    socket.on("no_room", () => {
      alert("Game Not Found");
      console.log("alert!");
    });

    // Clean up event listeners on unmount
    return () => {
      socket.off("room_joined");
      socket.off("room_code");
    };

};

function JoinRoom() {
  const [isHomescreen, setisHomescreen] = useState(true);

  const {socket,
    setInRoom,
    roomCode,
    setRoomCode,
    setIsWhite,
  } = usePlayerContext();

  // sets up listeners for room handling
  useEffect( () =>{
    roomListeners(socket, setInRoom, setRoomCode, setIsWhite, setisHomescreen);

  },[socket, setInRoom, setRoomCode, setIsWhite, setisHomescreen]);

  const isRoomValid = (event) => {
    event.preventDefault();
    socket.emit("is_room_valid?", roomCode);
  }

  return (
    <Wrapper>
      <Logo style={{ height: "20%", width: "auto" }}></Logo>
      <Description>A TEAM-BASED CHESS GAME</Description>
      {isHomescreen ?
        <MainLobbyPortal isRoomValid={isRoomValid} setisHomescreen = {setisHomescreen} /> :
        <UsernamePortal setisHomescreen = {setisHomescreen}/>
      }
    </Wrapper>
  ); 
};

export default JoinRoom;

const Description = styled.h2`
font-family: 'Montserrat';
font-style: normal;
font-weight:700;
font-size: 22px;
line-height: 40px;
color:#FFFFFF;
margin-bottom:30px;
margin-top:-10px;
`;


