import io from 'socket.io-client';
import { useState, Component, useEffect } from "react";
import { Chess } from "chess.js";
import Game from "./components/game"
import JoinRoom from "./components/JoinRoom"

const socket = io.connect("http://localhost:3001");
const game = new Chess();

function App() {
  const [isInRoom, setInRoom] = useState(false);
  const [roomCode, setRoomCode] = useState("");

  const joinRoom = () => {
    socket.emit("join_room", roomCode);
    setInRoom(true);
  }

  const newRoom = () => {
    socket.emit("new_room");
    setInRoom(true);
  }

  useEffect(() => {
    socket.on("room_code", (data) =>{
      setRoomCode(data);
    }
    )
}, []);

return (
  <div style={{ display: 'block', justifyContent: "center", alignItems: 'center', height: "100vh" }}>
    <h1>The Weakest Link</h1>
    {!isInRoom &&
      <JoinRoom joinRoom={joinRoom}
        roomCode={roomCode}
        setRoomCode={setRoomCode}
        newRoom={newRoom} />}
    {isInRoom && <Game socket={socket} game={game} />}
    <h1>Roomcode: {roomCode}</h1>

  </div>
);
}
export default App;
