import styled from 'styled-components';
import {Wrapper,Button} from "./index.js"

export const GameWrapper = styled(Wrapper)`
flex-flow:row-reverse;
justify-content:space-evenly;
`;

export const RoomCode = styled(Button)`
width:34%;
height:56px;
margin-left:10%;
background:#868BAC;
`;

export const TeamButton = styled(Button)`
height:50%;
width: 50%;
border: 3px solid rgba(151, 154, 175, 0.93);
background:${props => props.team ? "#E6E6E6" : "#939393"};
color:${props => props.team ? "#979AAF" : "#FFFFFF"};
margin:0;
`;

export const TeamName = styled.h2`
display:flex;
height:50%;
width:50%;
justify-content:center;
align-items:center;
margin:0;
font-family: 'Montserrat';
font-style: normal;
font-weight: 700;
font-size: 18px;
line-height: 40px;
color:${props => props.color ? "#E6E6E6" : "#939393"};
`;

const Teams = ({players,isWhite}) =>{
   const team = isWhite? 0:1;

  if (Object.keys(players).length === 0) return;
  else return(
    <ul>
      {players[team].map((player,i) => <li key = {i}> {player} </li>)}
    </ul>
  )
}
export const TeamsStyled = styled(Teams)`
${console.log("this is a test")}
${console.log(props => props.isWhite)}
${'' /* => isWhite "#979AAF" : "#FFFFFF" */}
`;
